insertion-points.methods:

public function generateMessageRef(?string $tag = null): MessageRef
{
    return new MessageRef(self::schema()->getCurie(), $this->fget(self::PROJECT_ID_FIELD), $tag);
}

public function getUriTemplateVars(): array
{
    return [
        'project_id' => $this->fget(self::PROJECT_ID_FIELD),
        'read_key' => $this->fget(self::READ_KEY_FIELD),
        'write_key' => $this->fget(self::WRITE_KEY_FIELD),
    ];
}


/**
 * @param {?string} tag
 * @returns {MessageRef}
 */
generateMessageRef(tag = null) {
  return new MessageRef(this.schema().getCurie(), this.get(this.PROJECT_ID_FIELD), tag);
}

/**
 * @returns {Object}
 */
getUriTemplateVars() {
  return {
    project_id: this.get(this.PROJECT_ID_FIELD),
    read_key: this.get(this.READ_KEY_FIELD),
    write_key: this.get(this.WRITE_KEY_FIELD),
  };
}
