insertion-points.methods:

public function generateMessageRef(?string $tag = null): MessageRef
{
    return new MessageRef(self::schema()->getCurie(), $this->fget(self::_ID_FIELD) ?: $this->generateEtag(), $tag);
}

public function getUriTemplateVars(): array
{
    return [
      '_id' => $this->fget(self::_ID_FIELD),
      'vendor' => $this->fget(self::VENDOR_FIELD),
      'name' => $this->fget(self::NAME_FIELD),
      'version' => $this->fget(self::VERSION_FIELD),
      'build' => $this->fget(self::BUILD_FIELD),
      'variant' => $this->fget(self::VARIANT_FIELD),
    ];
}


/**
 * @param {?string} tag
 * @returns {MessageRef}
 */
generateMessageRef(tag = null) {
  const id = this.get(this._ID_FIELD) || this.generateEtag();
  return new MessageRef(this.schema().getCurie(), `${id}`, tag);
}

/**
 * @returns {Object}
 */
getUriTemplateVars() {
  return {
    _id: `${this.get(this._ID_FIELD, '')}`,
    vendor: this.get(this.VENDOR_FIELD),
    name: this.get(this.NAME_FIELD),
    version: this.get(this.VERSION_FIELD),
    build: this.get(this.BUILD_FIELD),
    variant: this.get(this.VARIANT_FIELD),
  };
}
