insertion-points.methods:

/**
 * @param string $tag
 * @return MessageRef
 */
public function generateMessageRef($tag = null)
{
    return new MessageRef(static::schema()->getCurie(), $this->get('name'), $tag);
}

/**
 * @return array
 */
public function getUriTemplateVars()
{
    return ['name' => $this->get('name')];
}
