insertion-points.methods:

public function generateMessageRef(?string $tag = null): MessageRef
{
    return new MessageRef(self::schema()->getCurie(), $this->fget(self::GEO_HASH_FIELD), $tag);
}

public function getUriTemplateVars(): array
{
    return [
        'geo_hash' => $this->fget(self::GEO_HASH_FIELD),
    ];
}


/**
 * @param {?string} tag
 * @returns {MessageRef}
 */
generateMessageRef(tag = null) {
  return new MessageRef(this.schema().getCurie(), this.get(this.GEO_HASH_FIELD), tag);
},

/**
 * @returns {Object}
 */
getUriTemplateVars() {
  return {
    geo_hash: this.get(this.GEO_HASH_FIELD),
  };
}
