insertion-points.methods:

/**
 * @param string $tag
 * @return MessageRef
 */
public function generateMessageRef($tag = null)
{
    /** @var Edge $this */
    return new MessageRef(static::schema()->getCurie(), EdgeId::fromEdge($this), $tag);
}

/**
 * @return array
 */
public function getUriTemplateVars()
{
    /** @var Edge $this */
    return [
        'edge_id' => EdgeId::fromEdge($this)->toString(),
        'from_ref' => (string)$this->get('from_ref'),
        'to_ref' => (string)$this->get('to_ref'),
        'created_at' => (string)$this->get('created_at'),
    ];
}


/**
 * @param {?string} tag
 * @returns {MessageRef}
 */
generateMessageRef(tag = null) {
  return new MessageRef(this.schema().getCurie(), EdgeId.fromEdge(this), tag);
},

/**
 * @returns {Object}
 */
getUriTemplateVars() {
  return {
    edge_id: `${EdgeId.fromEdge(this)}`,
    from_ref: `${this.get('from_ref')}`,
    to_ref: `${this.get('to_ref')}`,
    created_at: `${this.get('created_at')}`,
  };
}
