insertion-points.methods:

/**
 * @param string $tag
 * @return MessageRef
 */
public function generateMessageRef($tag = null)
{
    return new MessageRef(static::schema()->getCurie(), $this->get('request_id'), $tag);
}

/**
 * @return array
 */
public function getUriTemplateVars()
{
    return [
        'request_id' => (string)$this->get('request_id'),
        'occurred_at' => (string)$this->get('occurred_at'),
        'ctx_user_ref' => (string)$this->get('ctx_user_ref'),
    ];
}


/**
 * @param {?string} tag
 * @returns {MessageRef}
 */
generateMessageRef(tag = null) {
  return new MessageRef(this.schema().getCurie(), this.get('request_id'), tag);
},

/**
 * @returns {Object}
 */
getUriTemplateVars() {
  return {
    request_id: `${this.get('request_id')}`,
    occurred_at: `${this.get('occurred_at')}`,
    ctx_user_ref: `${this.get('ctx_user_ref', '')}`,
  };
}
